#!/bin/bash
# Uninstall script for Hidden Bar
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Hidden Bar..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Hidden Bar if running..."
pkill -f "Hidden Bar" 2>/dev/null || true

# Unload service com.dwarvesv.LauncherApplication
echo "Unloading service com.dwarvesv.LauncherApplication..."
launchctl unload -w /Library/LaunchAgents/com.dwarvesv.LauncherApplication.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.dwarvesv.LauncherApplication.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.dwarvesv.LauncherApplication.plist 2>/dev/null || true

# Kill application with bundle ID com.dwarvesv.minimalbar if running
echo "Stopping application with bundle ID com.dwarvesv.minimalbar if running..."
killall -9 "com.dwarvesv.minimalbar" 2>/dev/null || true

# Remove /Applications/Hidden Bar.app
echo "Removing /Applications/Hidden Bar.app..."
if [ -d "/Applications/Hidden Bar.app" ]; then
    rm -rf "/Applications/Hidden Bar.app" 2>/dev/null || true
elif [ -f "/Applications/Hidden Bar.app" ]; then
    rm -f "/Applications/Hidden Bar.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.dwarvesv.LauncherApplication
echo "Removing $HOME/Library/Application Scripts/com.dwarvesv.LauncherApplication..."
if [ -d "$HOME/Library/Application Scripts/com.dwarvesv.LauncherApplication" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.dwarvesv.LauncherApplication" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.dwarvesv.LauncherApplication" ]; then
    rm -f "$HOME/Library/Application Scripts/com.dwarvesv.LauncherApplication" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.dwarvesv.minimalbar
echo "Removing $HOME/Library/Application Scripts/com.dwarvesv.minimalbar..."
if [ -d "$HOME/Library/Application Scripts/com.dwarvesv.minimalbar" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.dwarvesv.minimalbar" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.dwarvesv.minimalbar" ]; then
    rm -f "$HOME/Library/Application Scripts/com.dwarvesv.minimalbar" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.dwarvesv.LauncherApplication
echo "Removing $HOME/Library/Containers/com.dwarvesv.LauncherApplication..."
if [ -d "$HOME/Library/Containers/com.dwarvesv.LauncherApplication" ]; then
    rm -rf "$HOME/Library/Containers/com.dwarvesv.LauncherApplication" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.dwarvesv.LauncherApplication" ]; then
    rm -f "$HOME/Library/Containers/com.dwarvesv.LauncherApplication" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.dwarvesv.minimalbar
echo "Removing $HOME/Library/Containers/com.dwarvesv.minimalbar..."
if [ -d "$HOME/Library/Containers/com.dwarvesv.minimalbar" ]; then
    rm -rf "$HOME/Library/Containers/com.dwarvesv.minimalbar" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.dwarvesv.minimalbar" ]; then
