elif [ -f "$HOME/Library/Application Scripts/io.robbie.HomeAssistant" ]; then
    rm -f "$HOME/Library/Application Scripts/io.robbie.HomeAssistant" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/io.robbie.HomeAssistant
echo "Removing $HOME/Library/Containers/io.robbie.HomeAssistant..."
if [ -d "$HOME/Library/Containers/io.robbie.HomeAssistant" ]; then
