#!/bin/bash
# Uninstall script for Home Assistant
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Home Assistant..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Home Assistant if running..."
pkill -f "Home Assistant" 2>/dev/null || true

# Remove /Applications/Home Assistant.app
echo "Removing /Applications/Home Assistant.app..."
if [ -d "/Applications/Home Assistant.app" ]; then
    rm -rf "/Applications/Home Assistant.app" 2>/dev/null || true
elif [ -f "/Applications/Home Assistant.app" ]; then
    rm -f "/Applications/Home Assistant.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
