#!/bin/bash
# Uninstall script for HP Easy Admin
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling HP Easy Admin..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping HP Easy Admin if running..."
pkill -f "HP Easy Admin" 2>/dev/null || true

# Remove /Applications/HP Easy Admin.app
echo "Removing /Applications/HP Easy Admin.app..."
if [ -d "/Applications/HP Easy Admin.app" ]; then
    rm -rf "/Applications/HP Easy Admin.app" 2>/dev/null || true
elif [ -f "/Applications/HP Easy Admin.app" ]; then
    rm -f "/Applications/HP Easy Admin.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
