#!/bin/bash
# Uninstall script for Hyper
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Hyper..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Hyper if running..."
pkill -f "Hyper" 2>/dev/null || true

# Remove /Applications/Hyper.app
echo "Removing /Applications/Hyper.app..."
if [ -d "/Applications/Hyper.app" ]; then
    rm -rf "/Applications/Hyper.app" 2>/dev/null || true
elif [ -f "/Applications/Hyper.app" ]; then
    rm -f "/Applications/Hyper.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
