#!/bin/bash
# Uninstall script for Ice
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Ice..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Ice if running..."
pkill -f "Ice" 2>/dev/null || true

# Remove /Applications/Ice.app
echo "Removing /Applications/Ice.app..."
if [ -d "/Applications/Ice.app" ]; then
    rm -rf "/Applications/Ice.app" 2>/dev/null || true
elif [ -f "/Applications/Ice.app" ]; then
    rm -f "/Applications/Ice.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
