#!/bin/bash
# Uninstall script for Itsycal
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Itsycal..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Itsycal if running..."
pkill -f "Itsycal" 2>/dev/null || true

# Kill application with bundle ID com.mowglii.ItsycalApp if running
echo "Stopping application with bundle ID com.mowglii.ItsycalApp if running..."
killall -9 "com.mowglii.ItsycalApp" 2>/dev/null || true

# Remove /Applications/Itsycal.app
echo "Removing /Applications/Itsycal.app..."
if [ -d "/Applications/Itsycal.app" ]; then
    rm -rf "/Applications/Itsycal.app" 2>/dev/null || true
elif [ -f "/Applications/Itsycal.app" ]; then
    rm -f "/Applications/Itsycal.app" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.mowglii.ItsycalApp
echo "Removing $HOME/Library/HTTPStorages/com.mowglii.ItsycalApp..."
if [ -d "$HOME/Library/HTTPStorages/com.mowglii.ItsycalApp" ]; then
    rm -rf "$HOME/Library/HTTPStorages/com.mowglii.ItsycalApp" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/com.mowglii.ItsycalApp" ]; then
    rm -f "$HOME/Library/HTTPStorages/com.mowglii.ItsycalApp" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.mowglii.ItsycalApp.plist
echo "Removing $HOME/Library/Preferences/com.mowglii.ItsycalApp.plist..."
if [ -d "$HOME/Library/Preferences/com.mowglii.ItsycalApp.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.mowglii.ItsycalApp.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.mowglii.ItsycalApp.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.mowglii.ItsycalApp.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
