#!/bin/bash
# Uninstall script for Jellyfin
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Jellyfin..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Jellyfin if running..."
pkill -f "Jellyfin" 2>/dev/null || true

# Remove /Applications/Jellyfin.app
echo "Removing /Applications/Jellyfin.app..."
if [ -d "/Applications/Jellyfin.app" ]; then
    rm -rf "/Applications/Jellyfin.app" 2>/dev/null || true
elif [ -f "/Applications/Jellyfin.app" ]; then
    rm -f "/Applications/Jellyfin.app" 2>/dev/null || true
fi

# Remove /Library/Logs/DiagnosticReports/jellyfin*.diag
echo "Removing /Library/Logs/DiagnosticReports/jellyfin*.diag..."
if [ -d "/Library/Logs/DiagnosticReports/jellyfin*.diag" ]; then
    rm -rf "/Library/Logs/DiagnosticReports/jellyfin*.diag" 2>/dev/null || true
elif [ -f "/Library/Logs/DiagnosticReports/jellyfin*.diag" ]; then
    rm -f "/Library/Logs/DiagnosticReports/jellyfin*.diag" 2>/dev/null || true
fi

# Remove $HOME/.cache/jellyfin/
echo "Removing $HOME/.cache/jellyfin/..."
if [ -d "$HOME/.cache/jellyfin/" ]; then
    rm -rf "$HOME/.cache/jellyfin/" 2>/dev/null || true
elif [ -f "$HOME/.cache/jellyfin/" ]; then
    rm -f "$HOME/.cache/jellyfin/" 2>/dev/null || true
fi

# Remove $HOME/.config/jellyfin/
echo "Removing $HOME/.config/jellyfin/..."
if [ -d "$HOME/.config/jellyfin/" ]; then
    rm -rf "$HOME/.config/jellyfin/" 2>/dev/null || true
elif [ -f "$HOME/.config/jellyfin/" ]; then
    rm -f "$HOME/.config/jellyfin/" 2>/dev/null || true
fi

# Remove $HOME/.local/share/jellyfin/
echo "Removing $HOME/.local/share/jellyfin/..."
if [ -d "$HOME/.local/share/jellyfin/" ]; then
    rm -rf "$HOME/.local/share/jellyfin/" 2>/dev/null || true
elif [ -f "$HOME/.local/share/jellyfin/" ]; then
    rm -f "$HOME/.local/share/jellyfin/" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/jellyfin
echo "Removing $HOME/Library/Application Support/jellyfin..."
if [ -d "$HOME/Library/Application Support/jellyfin" ]; then
    rm -rf "$HOME/Library/Application Support/jellyfin" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/jellyfin" ]; then
    rm -f "$HOME/Library/Application Support/jellyfin" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Jellyfin.Server.plist
echo "Removing $HOME/Library/Preferences/Jellyfin.Server.plist..."
if [ -d "$HOME/Library/Preferences/Jellyfin.Server.plist" ]; then
    rm -rf "$HOME/Library/Preferences/Jellyfin.Server.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Jellyfin.Server.plist" ]; then
    rm -f "$HOME/Library/Preferences/Jellyfin.Server.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
