#!/bin/bash
# Uninstall script for JetBrains Toolbox
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling JetBrains Toolbox..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping JetBrains Toolbox if running..."
pkill -f "JetBrains Toolbox" 2>/dev/null || true

# Unload service com.jetbrains.toolbox
echo "Unloading service com.jetbrains.toolbox..."
launchctl unload -w /Library/LaunchAgents/com.jetbrains.toolbox.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.jetbrains.toolbox.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.jetbrains.toolbox.plist 2>/dev/null || true

# Remove /Applications/JetBrains Toolbox.app
echo "Removing /Applications/JetBrains Toolbox.app..."
if [ -d "/Applications/JetBrains Toolbox.app" ]; then
    rm -rf "/Applications/JetBrains Toolbox.app" 2>/dev/null || true
elif [ -f "/Applications/JetBrains Toolbox.app" ]; then
    rm -f "/Applications/JetBrains Toolbox.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/JetBrains/Toolbox
echo "Removing $HOME/Library/Application Support/JetBrains/Toolbox..."
if [ -d "$HOME/Library/Application Support/JetBrains/Toolbox" ]; then
    rm -rf "$HOME/Library/Application Support/JetBrains/Toolbox" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/JetBrains/Toolbox" ]; then
    rm -f "$HOME/Library/Application Support/JetBrains/Toolbox" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/JetBrains/Toolbox
echo "Removing $HOME/Library/Caches/JetBrains/Toolbox..."
if [ -d "$HOME/Library/Caches/JetBrains/Toolbox" ]; then
    rm -rf "$HOME/Library/Caches/JetBrains/Toolbox" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/JetBrains/Toolbox" ]; then
    rm -f "$HOME/Library/Caches/JetBrains/Toolbox" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/JetBrains/Toolbox
echo "Removing $HOME/Library/Logs/JetBrains/Toolbox..."
if [ -d "$HOME/Library/Logs/JetBrains/Toolbox" ]; then
    rm -rf "$HOME/Library/Logs/JetBrains/Toolbox" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/JetBrains/Toolbox" ]; then
    rm -f "$HOME/Library/Logs/JetBrains/Toolbox" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.jetbrains.toolbox.renderer.plist
echo "Removing $HOME/Library/Preferences/com.jetbrains.toolbox.renderer.plist..."
if [ -d "$HOME/Library/Preferences/com.jetbrains.toolbox.renderer.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.jetbrains.toolbox.renderer.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.jetbrains.toolbox.renderer.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.jetbrains.toolbox.renderer.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.jetbrains.toolbox.savedState
echo "Removing $HOME/Library/Saved Application State/com.jetbrains.toolbox.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.jetbrains.toolbox.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.jetbrains.toolbox.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.jetbrains.toolbox.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.jetbrains.toolbox.savedState" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/JetBrains
echo "Removing $HOME/Library/Application Support/JetBrains..."
if [ -d "$HOME/Library/Application Support/JetBrains" ]; then
    rm -rf "$HOME/Library/Application Support/JetBrains" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/JetBrains" ]; then
    rm -f "$HOME/Library/Application Support/JetBrains" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/JetBrains
echo "Removing $HOME/Library/Caches/JetBrains..."
if [ -d "$HOME/Library/Caches/JetBrains" ]; then
    rm -rf "$HOME/Library/Caches/JetBrains" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/JetBrains" ]; then
    rm -f "$HOME/Library/Caches/JetBrains" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/JetBrains
echo "Removing $HOME/Library/Logs/JetBrains..."
if [ -d "$HOME/Library/Logs/JetBrains" ]; then
    rm -rf "$HOME/Library/Logs/JetBrains" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/JetBrains" ]; then
    rm -f "$HOME/Library/Logs/JetBrains" 2>/dev/null || true
fi

