#!/bin/bash
# Uninstall script for Keybase
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Keybase..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Keybase if running..."
pkill -f "Keybase" 2>/dev/null || true

# Remove /Applications/Keybase.app
echo "Removing /Applications/Keybase.app..."
if [ -d "/Applications/Keybase.app" ]; then
    rm -rf "/Applications/Keybase.app" 2>/dev/null || true
elif [ -f "/Applications/Keybase.app" ]; then
    rm -f "/Applications/Keybase.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
