#!/bin/bash
# Uninstall script for KeyClu
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling KeyClu..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping KeyClu if running..."
pkill -f "KeyClu" 2>/dev/null || true

# Remove /Applications/KeyClu.app
echo "Removing /Applications/KeyClu.app..."
if [ -d "/Applications/KeyClu.app" ]; then
    rm -rf "/Applications/KeyClu.app" 2>/dev/null || true
elif [ -f "/Applications/KeyClu.app" ]; then
    rm -f "/Applications/KeyClu.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
