# Remove /Applications/LangGraph Studio.app
echo "Removing /Applications/LangGraph Studio.app..."
if [ -d "/Applications/LangGraph Studio.app" ]; then
    rm -rf "/Applications/LangGraph Studio.app" 2>/dev/null || true
elif [ -f "/Applications/LangGraph Studio.app" ]; then
    rm -f "/Applications/LangGraph Studio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/LangGraph Studio
