fi

# Remove $HOME/Library/Preferences/com.electron.langgraph-studio-desktop.plist
echo "Removing $HOME/Library/Preferences/com.electron.langgraph-studio-desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.electron.langgraph-studio-desktop.plist" ]; then
