#!/bin/bash
# Uninstall script for Lens
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Lens..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Lens if running..."
pkill -f "Lens" 2>/dev/null || true

# Remove /Applications/Lens.app
echo "Removing /Applications/Lens.app..."
if [ -d "/Applications/Lens.app" ]; then
    rm -rf "/Applications/Lens.app" 2>/dev/null || true
elif [ -f "/Applications/Lens.app" ]; then
    rm -f "/Applications/Lens.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Lens
echo "Removing $HOME/Library/Application Support/Lens..."
if [ -d "$HOME/Library/Application Support/Lens" ]; then
    rm -rf "$HOME/Library/Application Support/Lens" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Lens" ]; then
    rm -f "$HOME/Library/Application Support/Lens" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Lens
echo "Removing $HOME/Library/Caches/Lens..."
if [ -d "$HOME/Library/Caches/Lens" ]; then
    rm -rf "$HOME/Library/Caches/Lens" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Lens" ]; then
    rm -f "$HOME/Library/Caches/Lens" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.electron.kontena-lens.plist
echo "Removing $HOME/Library/Preferences/com.electron.kontena-lens.plist..."
if [ -d "$HOME/Library/Preferences/com.electron.kontena-lens.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.electron.kontena-lens.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.electron.kontena-lens.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.electron.kontena-lens.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.electron.kontena-lens.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.kontena-lens.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.kontena-lens.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.electron.kontena-lens.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.electron.kontena-lens.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.electron.kontena-lens.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
