#!/bin/bash
# Uninstall script for Logitech G HUB
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Logitech G HUB..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Logitech G HUB if running..."
pkill -f "Logitech G HUB" 2>/dev/null || true

# Unload service com.logi.ghub
echo "Unloading service com.logi.ghub..."
launchctl unload -w /Library/LaunchAgents/com.logi.ghub.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.logi.ghub.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.logi.ghub.plist 2>/dev/null || true

# Unload service com.logi.ghub.agent
echo "Unloading service com.logi.ghub.agent..."
launchctl unload -w /Library/LaunchAgents/com.logi.ghub.agent.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.logi.ghub.agent.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.logi.ghub.agent.plist 2>/dev/null || true

# Unload service com.logi.ghub.updater
echo "Unloading service com.logi.ghub.updater..."
launchctl unload -w /Library/LaunchAgents/com.logi.ghub.updater.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.logi.ghub.updater.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.logi.ghub.updater.plist 2>/dev/null || true

# Kill application with bundle ID com.logi.ghub if running
echo "Stopping application with bundle ID com.logi.ghub if running..."
killall -9 "com.logi.ghub" 2>/dev/null || true

# Kill application with bundle ID com.logi.ghub.agent if running
echo "Stopping application with bundle ID com.logi.ghub.agent if running..."
killall -9 "com.logi.ghub.agent" 2>/dev/null || true

# Kill application with bundle ID com.logi.ghub.updater if running
echo "Stopping application with bundle ID com.logi.ghub.updater if running..."
killall -9 "com.logi.ghub.updater" 2>/dev/null || true

# Remove $HOME/Library/Application Support/lghub
echo "Removing $HOME/Library/Application Support/lghub..."
if [ -d "$HOME/Library/Application Support/lghub" ]; then
    rm -rf "$HOME/Library/Application Support/lghub" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/lghub" ]; then
    rm -f "$HOME/Library/Application Support/lghub" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.logi.ghub.installer
echo "Removing $HOME/Library/Caches/com.logi.ghub.installer..."
if [ -d "$HOME/Library/Caches/com.logi.ghub.installer" ]; then
    rm -rf "$HOME/Library/Caches/com.logi.ghub.installer" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.logi.ghub.installer" ]; then
    rm -f "$HOME/Library/Caches/com.logi.ghub.installer" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.logi.ghub.plist
echo "Removing $HOME/Library/Preferences/com.logi.ghub.plist..."
if [ -d "$HOME/Library/Preferences/com.logi.ghub.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.logi.ghub.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.logi.ghub.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.logi.ghub.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.logi.ghub.savedState
echo "Removing $HOME/Library/Saved Application State/com.logi.ghub.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.logi.ghub.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.logi.ghub.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.logi.ghub.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.logi.ghub.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
