#!/bin/bash
# Uninstall script for LuLu
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling LuLu..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping LuLu if running..."
pkill -f "LuLu" 2>/dev/null || true

# Remove /Applications/LuLu.app
echo "Removing /Applications/LuLu.app..."
if [ -d "/Applications/LuLu.app" ]; then
    rm -rf "/Applications/LuLu.app" 2>/dev/null || true
elif [ -f "/Applications/LuLu.app" ]; then
    rm -f "/Applications/LuLu.app" 2>/dev/null || true
fi

