fi

# Remove $HOME/Library/Preferences/com.objective-see.lulu.plist
echo "Removing $HOME/Library/Preferences/com.objective-see.lulu.plist..."
if [ -d "$HOME/Library/Preferences/com.objective-see.lulu.plist" ]; then
