#!/bin/bash
# Uninstall script for Macs Fan Control
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Macs Fan Control..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Macs Fan Control if running..."
pkill -f "Macs Fan Control" 2>/dev/null || true

# Remove /Applications/Macs Fan Control.app
echo "Removing /Applications/Macs Fan Control.app..."
if [ -d "/Applications/Macs Fan Control.app" ]; then
    rm -rf "/Applications/Macs Fan Control.app" 2>/dev/null || true
elif [ -f "/Applications/Macs Fan Control.app" ]; then
    rm -f "/Applications/Macs Fan Control.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.crystalidea.macsfancontrol.plist
echo "Removing $HOME/Library/Preferences/com.crystalidea.macsfancontrol.plist..."
if [ -d "$HOME/Library/Preferences/com.crystalidea.macsfancontrol.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.crystalidea.macsfancontrol.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.crystalidea.macsfancontrol.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.crystalidea.macsfancontrol.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
