#!/bin/bash
# Uninstall script for Mattermost
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mattermost..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mattermost if running..."
pkill -f "Mattermost" 2>/dev/null || true

# Remove /Applications/Mattermost.app
echo "Removing /Applications/Mattermost.app..."
if [ -d "/Applications/Mattermost.app" ]; then
    rm -rf "/Applications/Mattermost.app" 2>/dev/null || true
elif [ -f "/Applications/Mattermost.app" ]; then
    rm -f "/Applications/Mattermost.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Mattermost
echo "Removing $HOME/Library/Application Support/Mattermost..."
if [ -d "$HOME/Library/Application Support/Mattermost" ]; then
    rm -rf "$HOME/Library/Application Support/Mattermost" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Mattermost" ]; then
    rm -f "$HOME/Library/Application Support/Mattermost" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/Mattermost.Desktop
echo "Removing $HOME/Library/Containers/Mattermost.Desktop..."
if [ -d "$HOME/Library/Containers/Mattermost.Desktop" ]; then
    rm -rf "$HOME/Library/Containers/Mattermost.Desktop" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/Mattermost.Desktop" ]; then
    rm -f "$HOME/Library/Containers/Mattermost.Desktop" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Mattermost
echo "Removing $HOME/Library/Logs/Mattermost..."
if [ -d "$HOME/Library/Logs/Mattermost" ]; then
    rm -rf "$HOME/Library/Logs/Mattermost" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Mattermost" ]; then
    rm -f "$HOME/Library/Logs/Mattermost" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Mattermost.Desktop.plist
echo "Removing $HOME/Library/Preferences/Mattermost.Desktop.plist..."
if [ -d "$HOME/Library/Preferences/Mattermost.Desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/Mattermost.Desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Mattermost.Desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/Mattermost.Desktop.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/Mattermost.Desktop.savedState
echo "Removing $HOME/Library/Saved Application State/Mattermost.Desktop.savedState..."
if [ -d "$HOME/Library/Saved Application State/Mattermost.Desktop.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/Mattermost.Desktop.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/Mattermost.Desktop.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/Mattermost.Desktop.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
