#!/bin/bash
# Uninstall script for MeetingBar
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MeetingBar..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MeetingBar if running..."
pkill -f "MeetingBar" 2>/dev/null || true

# Remove /Applications/MeetingBar.app
echo "Removing /Applications/MeetingBar.app..."
if [ -d "/Applications/MeetingBar.app" ]; then
    rm -rf "/Applications/MeetingBar.app" 2>/dev/null || true
elif [ -f "/Applications/MeetingBar.app" ]; then
    rm -f "/Applications/MeetingBar.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/leits.MeetingBar
echo "Removing $HOME/Library/Application Scripts/leits.MeetingBar..."
if [ -d "$HOME/Library/Application Scripts/leits.MeetingBar" ]; then
    rm -rf "$HOME/Library/Application Scripts/leits.MeetingBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/leits.MeetingBar" ]; then
    rm -f "$HOME/Library/Application Scripts/leits.MeetingBar" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/leits.MeetingBar
echo "Removing $HOME/Library/Containers/leits.MeetingBar..."
if [ -d "$HOME/Library/Containers/leits.MeetingBar" ]; then
    rm -rf "$HOME/Library/Containers/leits.MeetingBar" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/leits.MeetingBar" ]; then
    rm -f "$HOME/Library/Containers/leits.MeetingBar" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
