#!/bin/bash
# Uninstall script for MenuBar Stats
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MenuBar Stats..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MenuBar Stats if running..."
pkill -f "MenuBar Stats" 2>/dev/null || true

# Remove /Applications/MenuBar Stats.app
echo "Removing /Applications/MenuBar Stats.app..."
if [ -d "/Applications/MenuBar Stats.app" ]; then
    rm -rf "/Applications/MenuBar Stats.app" 2>/dev/null || true
elif [ -f "/Applications/MenuBar Stats.app" ]; then
    rm -f "/Applications/MenuBar Stats.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
