# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Microsoft Azure Storage Explorer if running..."
pkill -f "Microsoft Azure Storage Explorer" 2>/dev/null || true

# Remove /Applications/Microsoft Azure Storage Explorer.app
echo "Removing /Applications/Microsoft Azure Storage Explorer.app..."
if [ -d "/Applications/Microsoft Azure Storage Explorer.app" ]; then
    rm -rf "/Applications/Microsoft Azure Storage Explorer.app" 2>/dev/null || true
elif [ -f "/Applications/Microsoft Azure Storage Explorer.app" ]; then
    rm -f "/Applications/Microsoft Azure Storage Explorer.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
