#!/bin/bash
# Uninstall script for Microsoft Visual Studio Code
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Microsoft Visual Studio Code..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Microsoft Visual Studio Code if running..."
pkill -f "Microsoft Visual Studio Code" 2>/dev/null || true

# Remove /Applications/Visual Studio Code.app
echo "Removing /Applications/Visual Studio Code.app..."
if [ -d "/Applications/Visual Studio Code.app" ]; then
    rm -rf "/Applications/Visual Studio Code.app" 2>/dev/null || true
elif [ -f "/Applications/Visual Studio Code.app" ]; then
    rm -f "/Applications/Visual Studio Code.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
