#!/bin/bash
# Uninstall script for MiddleClick
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MiddleClick..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MiddleClick if running..."
pkill -f "MiddleClick" 2>/dev/null || true

# Kill application with bundle ID art.ginzburg.MiddleClick if running
echo "Stopping application with bundle ID art.ginzburg.MiddleClick if running..."
killall -9 "art.ginzburg.MiddleClick" 2>/dev/null || true

# Remove /Applications/MiddleClick.app
echo "Removing /Applications/MiddleClick.app..."
if [ -d "/Applications/MiddleClick.app" ]; then
    rm -rf "/Applications/MiddleClick.app" 2>/dev/null || true
elif [ -f "/Applications/MiddleClick.app" ]; then
    rm -f "/Applications/MiddleClick.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/art.ginzburg.MiddleClick.plist
echo "Removing $HOME/Library/Preferences/art.ginzburg.MiddleClick.plist..."
if [ -d "$HOME/Library/Preferences/art.ginzburg.MiddleClick.plist" ]; then
    rm -rf "$HOME/Library/Preferences/art.ginzburg.MiddleClick.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/art.ginzburg.MiddleClick.plist" ]; then
    rm -f "$HOME/Library/Preferences/art.ginzburg.MiddleClick.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
