#!/bin/bash
# Uninstall script for Mist
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mist..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mist if running..."
pkill -f "Mist" 2>/dev/null || true

# Unload service com.ninxsoft.mist.helper
echo "Unloading service com.ninxsoft.mist.helper..."
launchctl unload -w /Library/LaunchAgents/com.ninxsoft.mist.helper.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.ninxsoft.mist.helper.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.ninxsoft.mist.helper.plist 2>/dev/null || true

# Kill application with bundle ID com.ninxsoft.mist if running
echo "Stopping application with bundle ID com.ninxsoft.mist if running..."
killall -9 "com.ninxsoft.mist" 2>/dev/null || true

# Kill application with bundle ID com.ninxsoft.mist.helper if running
echo "Stopping application with bundle ID com.ninxsoft.mist.helper if running..."
killall -9 "com.ninxsoft.mist.helper" 2>/dev/null || true

# Remove /Users/Shared/Mist
echo "Removing /Users/Shared/Mist..."
if [ -d "/Users/Shared/Mist" ]; then
    rm -rf "/Users/Shared/Mist" 2>/dev/null || true
elif [ -f "/Users/Shared/Mist" ]; then
    rm -f "/Users/Shared/Mist" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.ninxsoft.mist
echo "Removing $HOME/Library/Caches/com.ninxsoft.mist..."
if [ -d "$HOME/Library/Caches/com.ninxsoft.mist" ]; then
    rm -rf "$HOME/Library/Caches/com.ninxsoft.mist" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.ninxsoft.mist" ]; then
    rm -f "$HOME/Library/Caches/com.ninxsoft.mist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.ninxsoft.mist.plist
echo "Removing $HOME/Library/Preferences/com.ninxsoft.mist.plist..."
if [ -d "$HOME/Library/Preferences/com.ninxsoft.mist.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.ninxsoft.mist.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.ninxsoft.mist.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.ninxsoft.mist.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.ninxsoft.mist.savedState
echo "Removing $HOME/Library/Saved Application State/com.ninxsoft.mist.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.ninxsoft.mist.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.ninxsoft.mist.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.ninxsoft.mist.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.ninxsoft.mist.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
