#!/bin/bash
# Uninstall script for Mockoon
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mockoon..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mockoon if running..."
pkill -f "Mockoon" 2>/dev/null || true

# Remove /Applications/Mockoon.app
echo "Removing /Applications/Mockoon.app..."
if [ -d "/Applications/Mockoon.app" ]; then
    rm -rf "/Applications/Mockoon.app" 2>/dev/null || true
elif [ -f "/Applications/Mockoon.app" ]; then
    rm -f "/Applications/Mockoon.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/mockoon
echo "Removing $HOME/Library/Application Support/mockoon..."
if [ -d "$HOME/Library/Application Support/mockoon" ]; then
    rm -rf "$HOME/Library/Application Support/mockoon" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/mockoon" ]; then
    rm -f "$HOME/Library/Application Support/mockoon" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Mockoon
echo "Removing $HOME/Library/Logs/Mockoon..."
if [ -d "$HOME/Library/Logs/Mockoon" ]; then
    rm -rf "$HOME/Library/Logs/Mockoon" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Mockoon" ]; then
    rm -f "$HOME/Library/Logs/Mockoon" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.mockoon.app.plist
echo "Removing $HOME/Library/Preferences/com.mockoon.app.plist..."
