echo "Removing $HOME/Library/Saved Application State/com.mockoon.app.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.mockoon.app.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.mockoon.app.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.mockoon.app.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.mockoon.app.savedState" 2>/dev/null || true
