#!/bin/bash
# Uninstall script for MonitorControl
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MonitorControl..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MonitorControl if running..."
pkill -f "MonitorControl" 2>/dev/null || true

# Remove /Applications/MonitorControl.app
echo "Removing /Applications/MonitorControl.app..."
if [ -d "/Applications/MonitorControl.app" ]; then
    rm -rf "/Applications/MonitorControl.app" 2>/dev/null || true
elif [ -f "/Applications/MonitorControl.app" ]; then
    rm -f "/Applications/MonitorControl.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/me.guillaumeb.MonitorControlHelper
echo "Removing $HOME/Library/Application Scripts/me.guillaumeb.MonitorControlHelper..."
if [ -d "$HOME/Library/Application Scripts/me.guillaumeb.MonitorControlHelper" ]; then
    rm -rf "$HOME/Library/Application Scripts/me.guillaumeb.MonitorControlHelper" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/me.guillaumeb.MonitorControlHelper" ]; then
    rm -f "$HOME/Library/Application Scripts/me.guillaumeb.MonitorControlHelper" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/me.guillaumeb.MonitorControlHelper
echo "Removing $HOME/Library/Containers/me.guillaumeb.MonitorControlHelper..."
if [ -d "$HOME/Library/Containers/me.guillaumeb.MonitorControlHelper" ]; then
    rm -rf "$HOME/Library/Containers/me.guillaumeb.MonitorControlHelper" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/me.guillaumeb.MonitorControlHelper" ]; then
