fi

# Remove $HOME/Library/Preferences/me.guillaumeb.MonitorControl.plist
echo "Removing $HOME/Library/Preferences/me.guillaumeb.MonitorControl.plist..."
if [ -d "$HOME/Library/Preferences/me.guillaumeb.MonitorControl.plist" ]; then
    rm -rf "$HOME/Library/Preferences/me.guillaumeb.MonitorControl.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/me.guillaumeb.MonitorControl.plist" ]; then
    rm -f "$HOME/Library/Preferences/me.guillaumeb.MonitorControl.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
