#!/bin/bash
# Uninstall script for Moonlight
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Moonlight..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Moonlight if running..."
pkill -f "Moonlight" 2>/dev/null || true

# Remove /Applications/Moonlight.app
echo "Removing /Applications/Moonlight.app..."
if [ -d "/Applications/Moonlight.app" ]; then
    rm -rf "/Applications/Moonlight.app" 2>/dev/null || true
elif [ -f "/Applications/Moonlight.app" ]; then
    rm -f "/Applications/Moonlight.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Moonlight Game Streaming Project
echo "Removing $HOME/Library/Caches/Moonlight Game Streaming Project..."
if [ -d "$HOME/Library/Caches/Moonlight Game Streaming Project" ]; then
    rm -rf "$HOME/Library/Caches/Moonlight Game Streaming Project" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Moonlight Game Streaming Project" ]; then
    rm -f "$HOME/Library/Caches/Moonlight Game Streaming Project" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.moonlight-stream.Moonlight.plist
echo "Removing $HOME/Library/Preferences/com.moonlight-stream.Moonlight.plist..."
if [ -d "$HOME/Library/Preferences/com.moonlight-stream.Moonlight.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.moonlight-stream.Moonlight.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.moonlight-stream.Moonlight.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.moonlight-stream.Moonlight.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.moonlight-stream.Moonlight.savedState
echo "Removing $HOME/Library/Saved Application State/com.moonlight-stream.Moonlight.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.moonlight-stream.Moonlight.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.moonlight-stream.Moonlight.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.moonlight-stream.Moonlight.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.moonlight-stream.Moonlight.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
