#!/bin/bash
# Uninstall script for Mos
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mos..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mos if running..."
pkill -f "Mos" 2>/dev/null || true

# Remove /Applications/Mos.app
echo "Removing /Applications/Mos.app..."
if [ -d "/Applications/Mos.app" ]; then
    rm -rf "/Applications/Mos.app" 2>/dev/null || true
elif [ -f "/Applications/Mos.app" ]; then
    rm -f "/Applications/Mos.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.caldis.Mos.plist
echo "Removing $HOME/Library/Preferences/com.caldis.Mos.plist..."
if [ -d "$HOME/Library/Preferences/com.caldis.Mos.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.caldis.Mos.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.caldis.Mos.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.caldis.Mos.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
