#!/bin/bash
# Uninstall script for Motrix
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Motrix..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Motrix if running..."
