# Remove /Applications/Motrix.app
echo "Removing /Applications/Motrix.app..."
if [ -d "/Applications/Motrix.app" ]; then
    rm -rf "/Applications/Motrix.app" 2>/dev/null || true
elif [ -f "/Applications/Motrix.app" ]; then
    rm -f "/Applications/Motrix.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Motrix
