elif [ -f "$HOME/Library/Preferences/app.motrix.native.plist" ]; then
    rm -f "$HOME/Library/Preferences/app.motrix.native.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/app.motrix.native.savedState
echo "Removing $HOME/Library/Saved Application State/app.motrix.native.savedState..."
