#!/bin/bash
# Uninstall script for mouseless
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling mouseless..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping mouseless if running..."
pkill -f "mouseless" 2>/dev/null || true

# Remove /Applications/Mouseless.app
echo "Removing /Applications/Mouseless.app..."
if [ -d "/Applications/Mouseless.app" ]; then
    rm -rf "/Applications/Mouseless.app" 2>/dev/null || true
elif [ -f "/Applications/Mouseless.app" ]; then
    rm -f "/Applications/Mouseless.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/net.sonuscape.mouseless
echo "Removing $HOME/Library/Application Scripts/net.sonuscape.mouseless..."
if [ -d "$HOME/Library/Application Scripts/net.sonuscape.mouseless" ]; then
    rm -rf "$HOME/Library/Application Scripts/net.sonuscape.mouseless" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/net.sonuscape.mouseless" ]; then
    rm -f "$HOME/Library/Application Scripts/net.sonuscape.mouseless" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/net.sonuscape.mouseless
echo "Removing $HOME/Library/Containers/net.sonuscape.mouseless..."
if [ -d "$HOME/Library/Containers/net.sonuscape.mouseless" ]; then
