#!/bin/bash
# Uninstall script for noTunes
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling noTunes..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping noTunes if running..."
pkill -f "noTunes" 2>/dev/null || true

# Remove /Applications/noTunes.app
echo "Removing /Applications/noTunes.app..."
if [ -d "/Applications/noTunes.app" ]; then
    rm -rf "/Applications/noTunes.app" 2>/dev/null || true
elif [ -f "/Applications/noTunes.app" ]; then
    rm -f "/Applications/noTunes.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/digital.twisted.noTunes.plist
echo "Removing $HOME/Library/Preferences/digital.twisted.noTunes.plist..."
if [ -d "$HOME/Library/Preferences/digital.twisted.noTunes.plist" ]; then
    rm -rf "$HOME/Library/Preferences/digital.twisted.noTunes.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/digital.twisted.noTunes.plist" ]; then
    rm -f "$HOME/Library/Preferences/digital.twisted.noTunes.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
