#!/bin/bash
# Uninstall script for Nucleo
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Nucleo..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Nucleo if running..."
pkill -f "Nucleo" 2>/dev/null || true

# Remove /Applications/Nucleo.app
echo "Removing /Applications/Nucleo.app..."
if [ -d "/Applications/Nucleo.app" ]; then
    rm -rf "/Applications/Nucleo.app" 2>/dev/null || true
elif [ -f "/Applications/Nucleo.app" ]; then
    rm -f "/Applications/Nucleo.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Nucleo
echo "Removing $HOME/Library/Application Support/Nucleo..."
if [ -d "$HOME/Library/Application Support/Nucleo" ]; then
    rm -rf "$HOME/Library/Application Support/Nucleo" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Nucleo" ]; then
    rm -f "$HOME/Library/Application Support/Nucleo" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Nucleo
echo "Removing $HOME/Library/Logs/Nucleo..."
if [ -d "$HOME/Library/Logs/Nucleo" ]; then
    rm -rf "$HOME/Library/Logs/Nucleo" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Nucleo" ]; then
    rm -f "$HOME/Library/Logs/Nucleo" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/co.ambercreative.nucleo.plist
echo "Removing $HOME/Library/Preferences/co.ambercreative.nucleo.plist..."
if [ -d "$HOME/Library/Preferences/co.ambercreative.nucleo.plist" ]; then
    rm -rf "$HOME/Library/Preferences/co.ambercreative.nucleo.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/co.ambercreative.nucleo.plist" ]; then
    rm -f "$HOME/Library/Preferences/co.ambercreative.nucleo.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/co.ambercreative.nucleo.savedState
echo "Removing $HOME/Library/Saved Application State/co.ambercreative.nucleo.savedState..."
if [ -d "$HOME/Library/Saved Application State/co.ambercreative.nucleo.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/co.ambercreative.nucleo.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/co.ambercreative.nucleo.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/co.ambercreative.nucleo.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
