#!/bin/bash
# Uninstall script for Obsidian
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Obsidian..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Obsidian if running..."
pkill -f "Obsidian" 2>/dev/null || true

# Remove /Applications/Obsidian.app
echo "Removing /Applications/Obsidian.app..."
if [ -d "/Applications/Obsidian.app" ]; then
    rm -rf "/Applications/Obsidian.app" 2>/dev/null || true
elif [ -f "/Applications/Obsidian.app" ]; then
    rm -f "/Applications/Obsidian.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/md.obsidian.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/md.obsidian.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/md.obsidian.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/md.obsidian.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/md.obsidian.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/md.obsidian.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/obsidian
echo "Removing $HOME/Library/Application Support/obsidian..."
if [ -d "$HOME/Library/Application Support/obsidian" ]; then
    rm -rf "$HOME/Library/Application Support/obsidian" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/obsidian" ]; then
    rm -f "$HOME/Library/Application Support/obsidian" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/md.obsidian.plist
echo "Removing $HOME/Library/Preferences/md.obsidian.plist..."
if [ -d "$HOME/Library/Preferences/md.obsidian.plist" ]; then
    rm -rf "$HOME/Library/Preferences/md.obsidian.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/md.obsidian.plist" ]; then
    rm -f "$HOME/Library/Preferences/md.obsidian.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/md.obsidian.savedState
echo "Removing $HOME/Library/Saved Application State/md.obsidian.savedState..."
if [ -d "$HOME/Library/Saved Application State/md.obsidian.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/md.obsidian.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/md.obsidian.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/md.obsidian.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
