#!/bin/bash
# Uninstall script for Ollama
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Ollama..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Ollama if running..."
pkill -f "Ollama" 2>/dev/null || true

# Remove /Applications/Ollama.app
echo "Removing /Applications/Ollama.app..."
if [ -d "/Applications/Ollama.app" ]; then
    rm -rf "/Applications/Ollama.app" 2>/dev/null || true
elif [ -f "/Applications/Ollama.app" ]; then
    rm -f "/Applications/Ollama.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
