#!/bin/bash
# Uninstall script for Ollama
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Ollama..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Ollama if running..."
pkill -f "Ollama" 2>/dev/null || true

# Remove /Applications/Ollama.app
echo "Removing /Applications/Ollama.app..."
if [ -d "/Applications/Ollama.app" ]; then
    rm -rf "/Applications/Ollama.app" 2>/dev/null || true
elif [ -f "/Applications/Ollama.app" ]; then
    rm -f "/Applications/Ollama.app" 2>/dev/null || true
fi

# Remove binary /Applications/Ollama.app/Ollama.app/Contents/Resources/ollama
echo "Removing binary /Applications/Ollama.app/Ollama.app/Contents/Resources/ollama..."
if [ -f "/Applications/Ollama.app/Ollama.app/Contents/Resources/ollama" ]; then
    rm -f "/Applications/Ollama.app/Ollama.app/Contents/Resources/ollama" 2>/dev/null || true
fi

# Remove $HOME/.ollama
echo "Removing $HOME/.ollama..."
if [ -d "$HOME/.ollama" ]; then
    rm -rf "$HOME/.ollama" 2>/dev/null || true
elif [ -f "$HOME/.ollama" ]; then
    rm -f "$HOME/.ollama" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Ollama
echo "Removing $HOME/Library/Application Support/Ollama..."
if [ -d "$HOME/Library/Application Support/Ollama" ]; then
    rm -rf "$HOME/Library/Application Support/Ollama" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Ollama" ]; then
    rm -f "$HOME/Library/Application Support/Ollama" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.electron.ollama.plist
echo "Removing $HOME/Library/Preferences/com.electron.ollama.plist..."
if [ -d "$HOME/Library/Preferences/com.electron.ollama.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.electron.ollama.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.electron.ollama.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.electron.ollama.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.electron.ollama.savedState
echo "Removing $HOME/Library/Saved Application State/com.electron.ollama.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.electron.ollama.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.electron.ollama.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.electron.ollama.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.electron.ollama.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
