#!/bin/bash
# Uninstall script for Parsec
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Parsec..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Parsec if running..."
pkill -f "Parsec" 2>/dev/null || true

# Kill application with bundle ID tv.parsec.www if running
echo "Stopping application with bundle ID tv.parsec.www if running..."
killall -9 "tv.parsec.www" 2>/dev/null || true

# Remove $HOME/.parsec
echo "Removing $HOME/.parsec..."
if [ -d "$HOME/.parsec" ]; then
    rm -rf "$HOME/.parsec" 2>/dev/null || true
elif [ -f "$HOME/.parsec" ]; then
    rm -f "$HOME/.parsec" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/tv.parsec.www
echo "Removing $HOME/Library/Caches/tv.parsec.www..."
if [ -d "$HOME/Library/Caches/tv.parsec.www" ]; then
    rm -rf "$HOME/Library/Caches/tv.parsec.www" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/tv.parsec.www" ]; then
    rm -f "$HOME/Library/Caches/tv.parsec.www" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/tv.parsec.www
echo "Removing $HOME/Library/HTTPStorages/tv.parsec.www..."
if [ -d "$HOME/Library/HTTPStorages/tv.parsec.www" ]; then
    rm -rf "$HOME/Library/HTTPStorages/tv.parsec.www" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/tv.parsec.www" ]; then
