#!/bin/bash
# Uninstall script for Pika
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Pika..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Pika if running..."
pkill -f "Pika" 2>/dev/null || true

# Remove /Applications/Pika.app
echo "Removing /Applications/Pika.app..."
if [ -d "/Applications/Pika.app" ]; then
    rm -rf "/Applications/Pika.app" 2>/dev/null || true
elif [ -f "/Applications/Pika.app" ]; then
    rm -f "/Applications/Pika.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.superhighfives.Pika-LaunchAtLoginHelper
echo "Removing $HOME/Library/Application Scripts/com.superhighfives.Pika-LaunchAtLoginHelper..."
if [ -d "$HOME/Library/Application Scripts/com.superhighfives.Pika-LaunchAtLoginHelper" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.superhighfives.Pika-LaunchAtLoginHelper" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.superhighfives.Pika-LaunchAtLoginHelper" ]; then
    rm -f "$HOME/Library/Application Scripts/com.superhighfives.Pika-LaunchAtLoginHelper" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.superhighfives.Pika-LaunchAtLoginHelper
echo "Removing $HOME/Library/Containers/com.superhighfives.Pika-LaunchAtLoginHelper..."
if [ -d "$HOME/Library/Containers/com.superhighfives.Pika-LaunchAtLoginHelper" ]; then
    rm -rf "$HOME/Library/Containers/com.superhighfives.Pika-LaunchAtLoginHelper" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.superhighfives.Pika-LaunchAtLoginHelper" ]; then
    rm -f "$HOME/Library/Containers/com.superhighfives.Pika-LaunchAtLoginHelper" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.superhighfives.Pika.plist
echo "Removing $HOME/Library/Preferences/com.superhighfives.Pika.plist..."
if [ -d "$HOME/Library/Preferences/com.superhighfives.Pika.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.superhighfives.Pika.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.superhighfives.Pika.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.superhighfives.Pika.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
