#!/bin/bash
# Uninstall script for Plex
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Plex..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Plex if running..."
pkill -f "Plex" 2>/dev/null || true

# Remove /Applications/Plex.app
echo "Removing /Applications/Plex.app..."
if [ -d "/Applications/Plex.app" ]; then
    rm -rf "/Applications/Plex.app" 2>/dev/null || true
elif [ -f "/Applications/Plex.app" ]; then
    rm -f "/Applications/Plex.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Plex
echo "Removing $HOME/Library/Application Support/Plex..."
if [ -d "$HOME/Library/Application Support/Plex" ]; then
    rm -rf "$HOME/Library/Application Support/Plex" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Plex" ]; then
    rm -f "$HOME/Library/Application Support/Plex" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Plex
echo "Removing $HOME/Library/Caches/Plex..."
if [ -d "$HOME/Library/Caches/Plex" ]; then
    rm -rf "$HOME/Library/Caches/Plex" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Plex" ]; then
    rm -f "$HOME/Library/Caches/Plex" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Plex
echo "Removing $HOME/Library/Logs/Plex..."
if [ -d "$HOME/Library/Logs/Plex" ]; then
    rm -rf "$HOME/Library/Logs/Plex" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Plex" ]; then
    rm -f "$HOME/Library/Logs/Plex" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
