#!/bin/bash
# Uninstall script for Postman
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Postman..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Postman if running..."
pkill -f "Postman" 2>/dev/null || true

# Remove /Applications/Postman.app
echo "Removing /Applications/Postman.app..."
if [ -d "/Applications/Postman.app" ]; then
    rm -rf "/Applications/Postman.app" 2>/dev/null || true
elif [ -f "/Applications/Postman.app" ]; then
    rm -f "/Applications/Postman.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
