#!/bin/bash
# Uninstall script for PowerShell
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling PowerShell..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping PowerShell if running..."
pkill -f "PowerShell" 2>/dev/null || true

# Remove $HOME/.cache/powershell
echo "Removing $HOME/.cache/powershell..."
if [ -d "$HOME/.cache/powershell" ]; then
    rm -rf "$HOME/.cache/powershell" 2>/dev/null || true
elif [ -f "$HOME/.cache/powershell" ]; then
    rm -f "$HOME/.cache/powershell" 2>/dev/null || true
fi

# Remove $HOME/.config/powershell
echo "Removing $HOME/.config/powershell..."
if [ -d "$HOME/.config/powershell" ]; then
    rm -rf "$HOME/.config/powershell" 2>/dev/null || true
elif [ -f "$HOME/.config/powershell" ]; then
    rm -f "$HOME/.config/powershell" 2>/dev/null || true
fi

# Remove $HOME/.local/share/powershell
echo "Removing $HOME/.local/share/powershell..."
if [ -d "$HOME/.local/share/powershell" ]; then
    rm -rf "$HOME/.local/share/powershell" 2>/dev/null || true
elif [ -f "$HOME/.local/share/powershell" ]; then
    rm -f "$HOME/.local/share/powershell" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
