#!/bin/bash
# Uninstall script for Principle
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Principle..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Principle if running..."
pkill -f "Principle" 2>/dev/null || true

# Remove /Applications/Principle.app
echo "Removing /Applications/Principle.app..."
if [ -d "/Applications/Principle.app" ]; then
    rm -rf "/Applications/Principle.app" 2>/dev/null || true
elif [ -f "/Applications/Principle.app" ]; then
    rm -f "/Applications/Principle.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
