#!/bin/bash
# Uninstall script for Proton Drive
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Proton Drive..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Proton Drive if running..."
pkill -f "Proton Drive" 2>/dev/null || true

# Remove /Applications/Proton Drive.app
echo "Removing /Applications/Proton Drive.app..."
if [ -d "/Applications/Proton Drive.app" ]; then
    rm -rf "/Applications/Proton Drive.app" 2>/dev/null || true
elif [ -f "/Applications/Proton Drive.app" ]; then
    rm -f "/Applications/Proton Drive.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/ch.protonmail.drive*
echo "Removing $HOME/Library/Application Scripts/ch.protonmail.drive*..."
if [ -d "$HOME/Library/Application Scripts/ch.protonmail.drive*" ]; then
    rm -rf "$HOME/Library/Application Scripts/ch.protonmail.drive*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/ch.protonmail.drive*" ]; then
    rm -f "$HOME/Library/Application Scripts/ch.protonmail.drive*" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/ch.protonmail.drive*
echo "Removing $HOME/Library/Containers/ch.protonmail.drive*..."
if [ -d "$HOME/Library/Containers/ch.protonmail.drive*" ]; then
    rm -rf "$HOME/Library/Containers/ch.protonmail.drive*" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/ch.protonmail.drive*" ]; then
    rm -f "$HOME/Library/Containers/ch.protonmail.drive*" 2>/dev/null || true
fi

# Remove $HOME/Library/Group Containers/group.ch.protonmail.protondrive
echo "Removing $HOME/Library/Group Containers/group.ch.protonmail.protondrive..."
if [ -d "$HOME/Library/Group Containers/group.ch.protonmail.protondrive" ]; then
    rm -rf "$HOME/Library/Group Containers/group.ch.protonmail.protondrive" 2>/dev/null || true
elif [ -f "$HOME/Library/Group Containers/group.ch.protonmail.protondrive" ]; then
    rm -f "$HOME/Library/Group Containers/group.ch.protonmail.protondrive" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
