#!/bin/bash
# Uninstall script for Proton Mail
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Proton Mail..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Proton Mail if running..."
pkill -f "Proton Mail" 2>/dev/null || true

# Remove /Applications/Proton Mail.app
echo "Removing /Applications/Proton Mail.app..."
if [ -d "/Applications/Proton Mail.app" ]; then
    rm -rf "/Applications/Proton Mail.app" 2>/dev/null || true
elif [ -f "/Applications/Proton Mail.app" ]; then
    rm -f "/Applications/Proton Mail.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Proton Mail
