#!/bin/bash
# Uninstall script for Remote Desktop Manager
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Remote Desktop Manager..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Remote Desktop Manager if running..."
pkill -f "Remote Desktop Manager" 2>/dev/null || true

# Remove /Applications/Remote Desktop Manager.app
echo "Removing /Applications/Remote Desktop Manager.app..."
if [ -d "/Applications/Remote Desktop Manager.app" ]; then
    rm -rf "/Applications/Remote Desktop Manager.app" 2>/dev/null || true
elif [ -f "/Applications/Remote Desktop Manager.app" ]; then
    rm -f "/Applications/Remote Desktop Manager.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.devolutions.remotedesktopmanager
