# Remove $HOME/Library/Preferences/com.devolutions.remotedesktopmanager.plist
echo "Removing $HOME/Library/Preferences/com.devolutions.remotedesktopmanager.plist..."
if [ -d "$HOME/Library/Preferences/com.devolutions.remotedesktopmanager.plist" ]; then
